use tv;

drop table sort;
create table sort(
	sid int(11) primary key auto_increment comment '分类id',
	sname varchar(100) not null comment '分类名'
);


select * from sort;

drop table tvSrc;
create table tvSrc(
	tid int(11) primary key auto_increment comment '播放源id',
	tname varchar(100) not null comment '资源名',
	tsrc longtext not null comment '地址',
	sid int(11) not null comment '分类id',
	foreign key(sid) references sort(sid)
);

	


